package Ch9_Exception_Handling;

/*
 * p. 312
 * An exception can be generated by one method and caught by other.
 *
 * Same as ExceptionDemo, but constructed in the different way, using static methods.
 */

class ExceptionTest {
    // Generate an exception
    static void generateException() {
        int nums[] = new int[4];
        System.out.println("Before exception is generated.");

        // Generate an index out-of-bounds exception
        nums[7] = 10;
        System.out.println("This won't be displayed.");
    }
}

public class ExceptionDemo2 {
    public static void main(String[] args) {

        try {
            /* This exception is called from ExceptionTest class
             * but cached in ExceptionDemo2 class by the main method.
             * If exception would have been caught in the generateException()
             * then it would never have been passed back to the main().
             */
            ExceptionTest.generateException();
        } catch (ArrayIndexOutOfBoundsException exception) {
            // catch the exception
            // When exception is caught exc object receives its value
            System.out.println("Index out-of-bounds!");
        }
        System.out.println("After catch statement.");
    }
}
